import React, { useState } from 'react';

function App() {
  const [podcastUrl, setPodcastUrl] = useState('');
  const [linkedInPost, setLinkedInPost] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLinkedInPost('');
    setError('');
    try {
      console.log('Sending request to:', '/api/generate-post');
      const response = await fetch('/api/generate-post', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ podcast_url: podcastUrl })
      });
      console.log('Response status:', response.status);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Received data:', data);
      setLinkedInPost(data.post);
    } catch (error) {
      console.error('Error details:', error);
      setError(`An error occurred: ${error.message}`);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h1>Podcast LinkedIn Post Generator</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={podcastUrl}
          onChange={(e) => setPodcastUrl(e.target.value)}
          placeholder="Enter podcast URL"
          required
          style={{ width: '100%', padding: '10px', marginBottom: '10px' }}
        />
        <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#0077B5', color: 'white', border: 'none' }}>
          Generate Post
        </button>
      </form>
      {linkedInPost && (
        <div>
          <h2>Generated LinkedIn Post:</h2>
          <p style={{ backgroundColor: '#f0f0f0', padding: '10px' }}>{linkedInPost}</p>
        </div>
      )}
      {error && <p style={{color: 'red'}}>{error}</p>}
    </div>
  );
}

export default App;
